if ( !msg.timestamp ) msg.timestamp = Math.round(+new Date());

var dt = new Date(msg.timestamp);
var options = {timeZone: 'Asia/Taipei', year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false };
var msg = {
	'payload':  dt.toLocaleString('zh-TW', options),
	'year':		dt.getFullYear(),
	'month':	dt.getMonth() + 1,
	'day':		dt.getDate(),
	'hours':	dt.getHours(),
	'mins':		dt.getMinutes(),
	'msecs':	dt.getMilliseconds()
}

return msg;
